angular.module("App",[]).controller("MainCtrl",["$scope","$http",function(t,o){t.noticias=null,o.get("https://pure-shore-2163.herokuapp.com/noticias").success(function(o){t.noticias=o.noticias,localStorage.setItem("noticias",JSON.stringify(t.noticias)),localStorage.setItem("minuto",(new Date).getMinutes())}).error(function(){t.noticias=JSON.parse(localStorage.getItem("noticias"))})}]).controller("ListContatoCtrl",["$scope","$http",function(t,o){t.items=null,o.get("contato.json").success(function(o){setInterval(function(){t.items=o},200)})}]);